import React, { Component } from 'react';
import './VerticalNavBar.css';

class VerticalNavBar extends Component {

    constructor(props) {
        super(props);

        this.state = {
          currPage: this.props.pages[0] //default is first
        };
    }

    selectPage(page) {
        document.getElementById(page.id).scrollIntoView({ behavior: 'smooth', block: "start" })
        this.setState({currPage: page})
    }

    render(){    
        return (
            <div className={"navbar " + (this.props.horizontal ? "navbar-horiz" : "")}>
                {/*display all the names of the pages as buttons that will scroll to the content*/}
                {this.props.pages.map(pg => 
                    <h4  className="page-name" id={(this.state.currPage == pg ? "selected" : "")}
                        onClick={() => this.selectPage(pg)}>
                             {pg.name}
                        </h4>)}
            </div>
        );
    }
}

export default VerticalNavBar;
