import React, { Component } from 'react';
import VerticalNavBar from './VerticalNavBar';
import Section from './Section';
import './PortfolioSite.css';

class PortfolioSite extends Component {

    constructor(props) {
        super(props);

        this.state = {
          currSectionIndex: 0
        };

        //initialize a map of all the pages I want to include
        this.pages = [
            {name: "WHO I AM", content: <Section className="sections" id="1"/>},
            {name: "CODE", content: <Section id="2"/>},
            {name: "UI/UX", content: <Section id="3"/>},
            {name: "STUDIO ART", content: <Section id="4"/>},
            {name: "RESUME", content: <Section id="5"/>}
        ]
      }

    render(){    
        return (
            <div className="site-container">

                <VerticalNavBar pages={this.pages}/>

                <div id="sections">
                    {this.pages.map(pg => pg.content)}
                </div>
            </div>
        );
    }
}

export default PortfolioSite;
