import React, { Component } from 'react';
import './Section.css';
import DisplayGrid from '../components/DisplayGrid';
import PopupButton from '../components/PopupButton';
import dv_thumbnail from '../images/dataverse/thumbnail_dv.png'; 
import netflix_thumbnail from '../images/netflix/netflix_thumbnail.png'; 
import poster_thumbnail from '../images/poster/poster_thumbnail.png'; 
import jam_thumbnail from '../images/jam-stage/jam_thumbnail.png';
import dash_thumbnail from '../images/dash/dash_thumbnail.png';

//page image imports
import dataverse_home from '../images/dataverse/dv_home_page.png';

class CodeProjects extends Component {


    // props are :headerText :list of body
    constructor(props) {
        super(props);

        this.state = {
            projects: this.getProjects()
        };
      }

    // all the code projects
    getProjects() {
        return [{title: "Dataverse", logline: "An online universe to teach kids about data structures", thumbnail: dv_thumbnail, languages: ["React, JS, Java, SQL"], launchLink: "https://dataverse.fun", pageHTML: this.getDataversePage()},
        {title: "Jam Stage", logline: "An audio-reactive environment made with Three.js", thumbnail: jam_thumbnail, languages: ["Three.js, WebAudio, JS"], launchLink: "https://laurawilsonri.github.io/jam-stage/", pageHTML: <div>DESCRIPTION</div>},
        {title: "COVID-19 Poster Generator", logline: "Customize your own social distancing poster", thumbnail: poster_thumbnail, languages: ["HTML, CSS, JS"], launchLink: "https://laurawilsonri.github.io/covid-poster-generator/", pageHTML: <div>DESCRIPTION</div>},
        {title: "Visualize Netflix in D3", logline: "A dashboard of Netflix data visualizations created with D3.js", thumbnail: netflix_thumbnail, languages: ["D3, JS, HTML"], launchLink: "https://laurawilsonri.github.io/netflix-visualizer/", pageHTML: <div>DESCRIPTION</div>},
        {title: "Dash", logline: "A collaborative online platform for media management", languages: ["React, TypeScript, C#"], thumbnail: dash_thumbnail, pageHTML: <div>DESCRIPTION</div>}
    ]
    }

    getDataversePage() {
       return <div className="project-expansion">
           <h3>Overview</h3>
           <p>This website aims to teach middle school students about data structures in a new, fun way!  
               Students fly around a rocket through dataverse, a universe where each planet offers a game that 
               is based on a specific data structure. Once they master single-player planets, they can compete against 
               their friends in the multiplayer arena or buy rocket skins and speed upgrades at the shop.</p>
            <h3>My Role</h3>
            <p>This website was the final project for my Software Engineering class, completed with 3 other students over the course of 4 weeks.</p>

            <p>The areas of the project I worked on included:
            <ul>
                <li>The frontend for the individual games (in Reactjs)</li>
                <li>The graphics, art, and iconography (in Adobe Illustrator)</li>
                <li>The backend integration and support for multiplayer</li>
            </ul>
            </p>

            <img src={dataverse_home}></img>
            <label>The welcome screen for dataverse informs users of the basic goal of the website and how to interact with the interface in a concise popup.</label>
            </div>;
            }

    render(){
        let project_count = 0;

        return (
            <div id="code-projects">
                <h1>What I've been Coding Up</h1>

                <DisplayGrid items={this.state.projects.map((p) => <PopupButton key={"project-count-" + project_count++} 
                    title={p.title} 
                    subtitle={p.logline} 
                    thumbnail={p.thumbnail} 
                    keywords={p.languages}
                    launchLink={p.launchLink}
                    pageHTML={p.pageHTML}/>)} />

            </div>
        );
    }
}

export default CodeProjects;
