import React, { Component } from 'react';
import VerticalNavBar from './VerticalNavBar';
import Section from './sections/Section';
import './PortfolioSite.css';

class PortfolioSite extends Component {

    constructor(props) {
        super(props);

        this.state = {
          currSectionIndex: 0
        };

        //initialize a map of all the pages I want to include
        this.pages = [
            {name: "WHO I AM", id: "who-i-am", content: <Section id="who-i-am" headerText="WHO I AM"/>},
            {name: "CODE", id: "code", content: <Section id="code"/>, id: "code"},
            {name: "DESIGN", id: "design", content: <Section id="uiux"/>, id: "design"},
            {name: "STUDIO ART", id: "art", content: <Section id="art"/>, id: "art"},
            {name: "RESUME", id: "resume", content: <Section id="resume"/>, id: "resume"}
        ]
      }

    render(){    
        return (
            <div className="site-container">

                <div className="portfolio-navbar">
                     <VerticalNavBar pages={this.pages}/>
                </div>

                <div id="sections">
                    {this.pages.map(pg => pg.content)}
                </div>
            </div>
        );
    }
}

export default PortfolioSite;
