import React, { Component } from 'react';
import './VerticalNavBar.css';

class VerticalNavBar extends Component {

    constructor(props) {
        super(props);

        this.state = {
          currPage: this.props.pages[0] //default is first
        };
    }

    render(){    
        return (
            <div className="navbar">
                {/*display all the names of the pages as buttons that will scroll to the content*/}
                {this.props.pages.map(pg => 
                    <h4  className="page-name ${if this.state.currPage.id==pg.id ? selected : not}" 
                        onClick={() => document.getElementById(pg.id).scrollIntoView({ behavior: 'smooth', block: "start" })}>
                             {pg.name}
                        </h4>)}
            </div>
        );
    }
}

export default VerticalNavBar;
