import React, { Component } from 'react';
import VerticalNavBar from './VerticalNavBar';
import WhoIAmPage from './sections/WhoIAmPage'
import Section from './sections/Section';
import './PortfolioSite.css';

class PortfolioSite extends Component {

    constructor(props) {
        super(props);

        this.state = {
          currSectionIndex: 0
        };

        //initialize a map of all the pages I want to include
        this.pages = [
            {name: "WHO I AM", id: "who-i-am", content: <WhoIAmPage/>},
            {name: "CODE", id: "code", content: <Section id="code"/>},
            {name: "DESIGN", id: "design", content: <Section id="design"/>},
            {name: "STUDIO ART", id: "art", content: <Section id="art"/>},
            {name: "RESUME", id: "resume", content: <Section id="resume"/>}
        ]
      }

    render(){    
        return (
            <div className="site-container">

                <div id="sections">
                    {this.pages.map(pg => pg.content)}
                </div>

                <div id="portfolio-navbar">
                     <VerticalNavBar pages={this.pages} onselect={pageSelected}/>
                </div>
            </div>
        );
    }
}

export default PortfolioSite;
