import React, { Component } from 'react';
import './Section.css';
import DisplayGrid from '../components/DisplayGrid';
import PopupButton from '../components/PopupButton';
import dv_thumbnail from '../images/dataverse/thumbnail_dv.png'; 

class CodeProjects extends Component {


    // props are :headerText :list of body
    constructor(props) {
        super(props);

        this.state = {
            projects: this.getProjects()
        };
      }

    // all the code projects
    getProjects() {
        return [{title: "Dataverse", logline: "An online universe to teach kids about data structures", thumbnail: dv_thumbnail, pageHTML: <div>DESCRIPTION</div>},
        {title: "Visualize Netflix in D3", logline: "A dashboard of Netflix data created with D3.js", pageHTML: <div>DESCRIPTION</div>},
        {title: "Jam Stage", logline: "An audio-reactive environment made with Three.js", pageHTML: <div>DESCRIPTION</div>},
        {title: "Dash", logline: "A collaborative online platform for media management", thumbnail: dv_thumbnail, pageHTML: <div>DESCRIPTION</div>}
    ]

    }

    render(){
        let project_count = 0;

        return (
            <div id="code-projects">
                <h1>What I've been Coding Up</h1>

                <DisplayGrid items={this.state.projects.map((p) => <PopupButton key={"project-count-" + project_count++} title={p.title} subtitle={p.logline} thumbnail={dv_thumbnail} pageHTML={p.pageHTML}/>)} />

            </div>
        );
    }
}

export default CodeProjects;
